import {useEffect, useState} from "react";
import styles from "../styles/schedule.module.css";
import {Button} from "@material-ui/core";
import {NavLink} from "react-router-dom";

const Standings = ({url, leagueId}) => {
    const [standings, setStandings] = useState([])
    const [stages, setStages] = useState([])

    useEffect(() => {
        fetch(`${url}/standings?leagueId=${leagueId}`)
            .then(response => response.json())
            .then(result => setStandings(result.standings))

        setStages(standings.stages)
    }, [url, leagueId, standings.stages])

    return(
        <div className={styles.container}>
            <nav className={styles.navbar}>
                {stages.map(stage => (
                    <Button className={styles.link}>
                        <NavLink to={`/${standings.slug}/`}>Schedule</NavLink>
                    </Button>
                ))}
            </nav>
        </div>
    )
}

export default Standings
